import React, { useState, useEffect } from 'react';
import { Shuffle, Plus, Minus, X, RotateCcw, LogOut, Moon, Sun, Smartphone, QrCode } from 'lucide-react';
import QRCode from 'qrcode';
import './index.css';

type Matrix = number[][];

interface OperationStep {
  description: string;
  formula?: string;
  result?: number;
}

const MatrixCalculator: React.FC = () => {
  const [matrixA, setMatrixA] = useState<Matrix>(Array(3).fill(null).map(() => Array(3).fill(0)));
  const [matrixB, setMatrixB] = useState<Matrix>(Array(3).fill(null).map(() => Array(3).fill(0)));
  const [resultMatrix, setResultMatrix] = useState<Matrix | null>(null);
  const [operationSteps, setOperationSteps] = useState<OperationStep[]>([]);
  const [currentOperation, setCurrentOperation] = useState<string>('');
  const [showProcess, setShowProcess] = useState<boolean>(false);
  const [darkMode, setDarkMode] = useState<boolean>(false);

  // Cargar preferencia de tema desde localStorage
  useEffect(() => {
    const savedTheme = localStorage.getItem('darkMode');
    if (savedTheme) {
      setDarkMode(JSON.parse(savedTheme));
    }
  }, []);

  // Guardar preferencia de tema y aplicar clase al body
  useEffect(() => {
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
    if (darkMode) {
      document.body.classList.add('dark');
    } else {
      document.body.classList.remove('dark');
    }
  }, [darkMode]);

  const fillMatrixRandom = (matrix: Matrix): Matrix => {
    return matrix.map(row => 
      row.map(() => Math.floor(Math.random() * 10))
    );
  };

  const handleFillBothMatrices = () => {
    const newMatrixA = fillMatrixRandom(matrixA);
    const newMatrixB = fillMatrixRandom(matrixB);
    setMatrixA(newMatrixA);
    setMatrixB(newMatrixB);
    setResultMatrix(null);
    setOperationSteps([]);
  };

  const addMatrices = () => {
    const steps: OperationStep[] = [];
    steps.push({
      description: 'Suma de Matrices',
      formula: 'C[i][j] = A[i][j] + B[i][j]'
    });

    const result: Matrix = matrixA.map((row, i) => {
      return row.map((val, j) => {
        const sum = val + matrixB[i][j];
        steps.push({
          description: `Posición C${i+1}${j+1}`,
          formula: `${val} + ${matrixB[i][j]} = ${sum}`,
          result: sum
        });
        return sum;
      });
    });

    setResultMatrix(result);
    setOperationSteps(steps);
    setCurrentOperation('suma');
    setShowProcess(true);
  };

  const subtractMatrices = () => {
    const steps: OperationStep[] = [];
    steps.push({
      description: 'Resta de Matrices',
      formula: 'C[i][j] = A[i][j] - B[i][j]'
    });

    const result: Matrix = matrixA.map((row, i) => {
      return row.map((val, j) => {
        const diff = val - matrixB[i][j];
        steps.push({
          description: `Posición C${i+1}${j+1}`,
          formula: `${val} - ${matrixB[i][j]} = ${diff}`,
          result: diff
        });
        return diff;
      });
    });

    setResultMatrix(result);
    setOperationSteps(steps);
    setCurrentOperation('resta');
    setShowProcess(true);
  };

  const multiplyMatrices = () => {
    const steps: OperationStep[] = [];
    steps.push({
      description: 'Multiplicación de Matrices',
      formula: 'C[i][j] = Σ(A[i][k] × B[k][j])'
    });

    const result: Matrix = Array(3).fill(null).map(() => Array(3).fill(0));

    for (let i = 0; i < 3; i++) {
      for (let j = 0; j < 3; j++) {
        let sum = 0;
        let formula = '';

        for (let k = 0; k < 3; k++) {
          const product = matrixA[i][k] * matrixB[k][j];
          sum += product;
          formula += k === 0 ? `${matrixA[i][k]}×${matrixB[k][j]}` : ` + ${matrixA[i][k]}×${matrixB[k][j]}`;
        }

        steps.push({
          description: `Posición C${i+1}${j+1}`,
          formula: `${formula} = ${sum}`,
          result: sum
        });

        result[i][j] = sum;
      }
    }

    setResultMatrix(result);
    setOperationSteps(steps);
    setCurrentOperation('multiplicación');
    setShowProcess(true);
  };

  const renderMatrix = (matrix: Matrix, title: string, bgColor: string = 'bg-white') => (
    <div className={`${bgColor} dark:bg-gray-800 rounded-2xl shadow-lg p-6 transform transition-all duration-300 hover:scale-105 hover:shadow-xl border border-gray-100 dark:border-gray-700`}>
      <h3 className="text-xl font-semibold text-center mb-4 text-gray-800 dark:text-gray-100 transition-colors duration-300">{title}</h3>
      <div className="grid grid-cols-3 gap-2 max-w-xs mx-auto">
        {matrix.map((row, i) =>
          row.map((val, j) => (
            <div
              key={`${i}-${j}`}
              className={`matrix-cell ${bgColor === 'bg-white' ? 'bg-gradient-to-br from-gray-50 to-gray-100 dark:from-gray-700 dark:to-gray-600 text-gray-800 dark:text-gray-100 border border-gray-300 dark:border-gray-600' : 'bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-blue-900 dark:to-indigo-900 text-blue-800 dark:text-blue-100 border border-blue-300 dark:border-blue-700'}`}
            >
              {val}
            </div>
          ))
        )}
      </div>
    </div>
  );

  const resetCalculator = () => {
    setMatrixA(Array(3).fill(null).map(() => Array(3).fill(0)));
    setMatrixB(Array(3).fill(null).map(() => Array(3).fill(0)));
    setResultMatrix(null);
    setOperationSteps([]);
    setCurrentOperation('');
    setShowProcess(false);
  };

  return (
    <div className={`min-h-screen py-8 px-4 relative transition-colors duration-300 ${darkMode ? 'dark' : ''}`}>
      {/* Header con información del estudiante y toggle de tema */}
      <div className="absolute top-4 left-4 text-left">
        <p className="text-sm font-medium text-gray-600 dark:text-gray-300">Angel David Arriaga Gonzalez</p>
        <p className="text-xs text-gray-500 dark:text-gray-400">Álgebra Lineal</p>
      </div>

      {/* Toggle de modo oscuro */}
      <button
        onClick={() => setDarkMode(!darkMode)}
        className="absolute top-4 right-4 p-3 rounded-full bg-white dark:bg-gray-800 shadow-lg hover:shadow-xl transition-all duration-300 hover:scale-110 border border-gray-200 dark:border-gray-700"
        aria-label="Toggle dark mode"
      >
        {darkMode ? (
          <Sun size={20} className="text-yellow-500" />
        ) : (
          <Moon size={20} className="text-gray-700" />
        )}
      </button>

      <div className="max-w-6xl mx-auto">
        <header className="text-center mb-10 animate-fade-in">
          <h1 className="text-4xl font-semibold text-gray-800 dark:text-gray-100 mb-3 transition-colors duration-300">
            Calculadora de Matrices
          </h1>
          <p className="text-gray-600 dark:text-gray-400 text-lg transition-colors duration-300">Operaciones con matrices 3×3</p>
        </header>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mb-8">
          {renderMatrix(matrixA, 'Matriz A', 'bg-white')}
          {renderMatrix(matrixB, 'Matriz B', 'bg-white')}
          {resultMatrix && renderMatrix(resultMatrix, 'Matriz Resultado', 'bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-blue-900 dark:to-indigo-900')}
        </div>

        <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 mb-8 border border-gray-100 dark:border-gray-700 transition-colors duration-300">
          <h2 className="text-2xl font-semibold text-center mb-6 text-gray-800 dark:text-gray-100 transition-colors duration-300">Operaciones</h2>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
            <button
              onClick={handleFillBothMatrices}
              className="bg-gradient-to-r from-slate-100 to-gray-100 dark:from-gray-700 dark:to-gray-600 hover:from-slate-200 hover:to-gray-200 dark:hover:from-gray-600 dark:hover:to-gray-500 text-gray-700 dark:text-gray-200 px-6 py-3 rounded-xl font-medium transition-all duration-200 hover:shadow-md hover:-translate-y-0.5 flex items-center justify-center gap-2"
            >
              <Shuffle size={18} />
              Llenar Matrices
            </button>
            <button
              onClick={addMatrices}
              className="bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 text-white px-6 py-3 rounded-xl font-medium transition-all duration-200 hover:shadow-md hover:-translate-y-0.5 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none flex items-center justify-center gap-2"
              disabled={!matrixA.some(row => row.some(val => val !== 0)) || !matrixB.some(row => row.some(val => val !== 0))}
            >
              <Plus size={18} />
              Sumar
            </button>
            <button
              onClick={subtractMatrices}
              className="bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 text-white px-6 py-3 rounded-xl font-medium transition-all duration-200 hover:shadow-md hover:-translate-y-0.5 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none flex items-center justify-center gap-2"
              disabled={!matrixA.some(row => row.some(val => val !== 0)) || !matrixB.some(row => row.some(val => val !== 0))}
            >
              <Minus size={18} />
              Restar
            </button>
            <button
              onClick={multiplyMatrices}
              className="bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 text-white px-6 py-3 rounded-xl font-medium transition-all duration-200 hover:shadow-md hover:-translate-y-0.5 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none flex items-center justify-center gap-2"
              disabled={!matrixA.some(row => row.some(val => val !== 0)) || !matrixB.some(row => row.some(val => val !== 0))}
            >
              <X size={18} />
              Multiplicar
            </button>
            <button
              onClick={resetCalculator}
              className="bg-gradient-to-r from-gray-500 to-gray-600 hover:from-gray-600 hover:to-gray-700 text-white px-6 py-3 rounded-xl font-medium transition-all duration-200 hover:shadow-md hover:-translate-y-0.5 flex items-center justify-center gap-2"
            >
              <RotateCcw size={18} />
              Reiniciar
            </button>
            <button
              onClick={() => window.close()}
              className="bg-gradient-to-r from-red-500 to-red-600 hover:from-red-600 hover:to-red-700 text-white px-6 py-3 rounded-xl font-medium transition-all duration-200 hover:shadow-md hover:-translate-y-0.5 flex items-center justify-center gap-2"
            >
              <LogOut size={18} />
              Salir
            </button>
          </div>
        </div>

        {showProcess && operationSteps.length > 0 && (
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 animate-slide-up border border-gray-100 dark:border-gray-700 transition-colors duration-300">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-semibold text-gray-800 dark:text-gray-100 transition-colors duration-300">
                Proceso de {currentOperation}
              </h2>
              <button
                onClick={() => setShowProcess(false)}
                className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 text-2xl font-light transition-colors duration-200"
              >
                ×
              </button>
            </div>

            <div className="space-y-3 max-h-96 overflow-y-auto">
              {operationSteps.map((step, index) => (
                <div key={index} className="bg-gradient-to-r from-gray-50 to-slate-50 dark:from-gray-700 dark:to-gray-600 rounded-xl p-4 border border-gray-200 dark:border-gray-600 hover:shadow-md transition-all duration-200">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <h4 className="font-semibold text-gray-800 dark:text-gray-100 mb-2 transition-colors duration-300">
                        {step.description}
                      </h4>
                      {step.formula && (
                        <p className="text-base font-mono text-blue-600 dark:text-blue-400 bg-blue-50 dark:bg-blue-900 px-3 py-2 rounded-lg border border-blue-200 dark:border-blue-700 transition-colors duration-300">
                          {step.formula}
                        </p>
                      )}
                    </div>
                    {step.result !== undefined && (
                      <div className="bg-gradient-to-r from-green-50 to-emerald-50 dark:from-green-900 dark:to-emerald-900 text-green-700 dark:text-green-300 px-4 py-2 rounded-xl font-semibold text-base ml-4 border border-green-200 dark:border-green-700 transition-colors duration-300">
                        = {step.result}
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MatrixCalculator;
